import { ScrollView } from 'react-native';
import { Box, Text } from '@gluestack-ui/themed';


export const Home = () => {
    return <Container />
  };
  
  const FeatureCard = ({ iconSvg: name, desc, numero, Data, total}: any) => {
    return (
      <Box
        flexDirection="column"
        borderWidth={1}
        borderColor="$borderDark700"
        flex={1}
        m="$2"
        p="$4"
        rounded="$md"
      >
        <Box alignItems="center" display="flex" flexDirection="column">
         
          
        </Box>

        <Text  fontSize={22} color="$white" fontWeight="500" ml="$2">
          {numero}
        </Text>
        <Text color="$textDark400" mt="$2">
          {desc}
        </Text>

        <Text color="$textDark400" mt="$2">
          {Data}
        </Text>
        <Text  fontSize={15} color="$white" fontWeight="500" ml="$2">
          {total}
        </Text>

        
      </Box>
      
    );
  };
  
  const Container = () => {
    return (
      <Box flex={1} backgroundColor="black">
        <ScrollView
          style={{ height: '100%' }}
          contentContainerStyle={{ flexGrow: 1 }}
        >
          <Box
            position="absolute"
            sx={{
              '@base': {
                h: 500,
                w: 500,
              },
              '@lg': {
                h: 700,
                w: 700,
              },
            }}
          >
          </Box>
          <Box
            height="60%"
            sx={{
              '@base': {
                my: '$16',
                mx: '$5',
                height: '80%',
              },
              '@lg': {
                my: '$24',
                mx: '$32',
              },
            }}
            justifyContent='space-around'
            alignItems="flex-start"
          >
            


            <Box justifyContent="center" alignItems="center">
                
              <Text color='white'>Vendas</Text>
            </Box>

            <Box
              sx={{
                '@base': {
                  flexDirection: 'column',
                },
                '@md': {
                  flexDirection: 'column',
                },
              }}
            >
              <FeatureCard
                numero ="Pedido: 19491"
                desc="Cliente: Consumidor Final"
                Data="23/09/2023"
                total="Total(R$): 150,00"
              />
              <FeatureCard
                numero ="Pedido: 19491"
                desc="Cliente: Consumidor Final"
                Data="23/09/2023"
                total="Total(R$): 150,00"
              />
              <FeatureCard
                numero ="Pedido: 19491"
                desc="Cliente: Consumidor Final"
                Data="23/09/2023"
                total="Total(R$): 150,00"
              />
            </Box>
          </Box>

          
        </ScrollView>
      </Box>
    );
  };
